package basics;

public class Simulator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Datatypes dataType = new Datatypes();
		System.out.println("WHOLE NUMBERS PRIMITIVE TYPES");
		System.out.println("char value:" + dataType.myChar);
		System.out.println("int value:" + dataType.myInt);
		System.out.println("byte value:" + dataType.myByte);
		System.out.println("short value:" + dataType.myShort);
		System.out.println("long value:" + dataType.myLong);
		System.out.println("\n");
		System.out.println("DECIMALS PRIMITIVE TYPES");
		System.out.println("float value:" + dataType.myFloat);
		System.out.println("double value:" + dataType.myDouble);
		System.out.println("\n");
		System.out.println("OTHER PRIMITIVE TYPES");
		System.out.println("boolean value:" + dataType.myBoolean);
		

	}

}
