package basics;

public class casting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 65;
		double decimal = num;
		
		double decimal_2 = 65.0;
		int num_2 = (int) decimal_2;
		
		char letter = (char)num;
		System.out.println(letter);
		
		byte b = (byte)255;
		System.out.println(b);
		
		
	}

}
